//SPDX-License-Identifier: GPL-2.0
pragma solidity >=0.0;
pragma abicoder v2;

contract C
{
	int constant constVar = 41;
	int immutable immutVar = 42;
	int public stateVar;

	constructor(int _init)
	{
		stateVar = _init;
	}

	function f() external pure returns (int)
	{
		return constVar + immutVar;
	}
	modifier m()
	{
		stateVar++;
		_;
	}
	function f2() m public returns (int)
	{
		return stateVar + this.f() + immutVar;
	}
}

